(function(){angular.module("portfolioApp").controller("LanguagesController",function($scope,Labels,Languages){var serverErrorHandler;return $scope.init=function(){return $scope.languages=Languages.getCurrent(),$scope.labels=Labels.getCurrent(),$scope.currentLanguage=Languages.getCurrentLanguage(),$scope.icon_color=$scope.labels[0][$scope.currentLanguage].config.icon_color},$scope.setCurrentLanguage=function(lang){return Languages.setCurrentLanguage(lang),$scope.currentLanguage=Languages.getCurrentLanguage(),$scope.icon_color=$scope.labels[0][$scope.currentLanguage].config.icon_color,$scope.$apply()},$scope.getIconColor=function(){return $scope.icon_color},serverErrorHandler=function(){return alert("Server error in LanguagesController, please try again!")}})}).call(this);