(function(){angular.module("portfolioApp").controller("UserController",function($scope,Users,Languages,Labels,Section){var serverErrorHandler;return $scope.init=function(){return this.usersService=new Users(serverErrorHandler),$scope.users=this.usersService.all(),$scope.labels=Labels.getCurrent(),$scope.languages=Languages.getCurrent(),$scope.currentLanguage=Languages.getCurrentLanguage(),$scope.label=$scope.labels[0][$scope.currentLanguage]},$scope.popover_message=function(title,links,attachments,awards){return Section.popover_message(title,links,attachments,[],awards,$scope.label)},$scope.setCurrentLanguage=function(){return $scope.currentLanguage=Languages.getCurrentLanguage(),$scope.label=$scope.labels[0][$scope.currentLanguage]},serverErrorHandler=function(){return alert("Server error in UserController, please try again!")}})}).call(this);